import {useState, useEffect} from 'react'
import axios from 'axios'
import {submitStyles, inputStyles, formStyles} from './styles.js'

const PasswordErrorMessage =(error)=> <div style={{color:'red'}}>{error}</div>

export default function Register(){
	const [username, setUsername] = useState('')
	const [password, setPassword] = useState('')
	const [password1, setPassword1] = useState('')
	const [error, setError] = useState('')

//	useEffect(()=>{
//		setPasswordErr(false)
//	}, [ username, password, password1])

	const handleSubmit = e => {
		e.preventDefault()
		if (password !== password1){
			setError('You passwords are not equal.')
		}
		else if(!(username || password || password1)){	
			setError('Fill all of the fields')
		}
	}

	return(
	<div>

	<form style={formStyles} onSubmit={e => handleSubmit(e)}>
		<label htmlFor='username'>Enter your username:</label>
		<input value={username} name='usersname' onChange={e => setUsername(e.target.value)} style={inputStyles}/>

		<label htmlFor='password'>Enter your password:</label>
		<input value={password} name='password' type='password' onChange={e => setPassword(e.target.value)} style={inputStyles}/>

		<label htmlFor="password1">Confirm your password:</label>
		<input value={password1} name='password1' type='password' onChange={e => setPassword1(e.target.value)} style={inputStyles}/>
		{error ?

		<PasswordErrorMessage error={error}/>	:
			null}
		<input type='submit' value='Register' style={submitStyles}/>
	</form>
	</div>

	)

}
