import './App.css';
import axios from 'axios'
import {useEffect, useState} from 'react'
import {csrftoken} from './Token.js'

const submitStyles = {
	padding:'15px',
	background:'rgb(103,29,223)',
	color:'white',
	borderRadius:'5px',
	border:'none',
	fontSize:'20px',
	width:'150px'
}

const inputStyles = {
	margin: '20px',
	width:'200px',
	height:'30px',
	fontSize:'20px'
}


const formStyles = {
	display:'flex',
	flexDirection: 'column',
	justifyContent:'center',
	alignItems:'center',
	margin:''
}


function App() {

	const [message, setMessage] = useState('')
	const [token, setToken] = useState('')
	const [username, setUsername] = useState('')
	const [password, setPassword] = useState('')

	useEffect(()=>{
		if (token){
		axios({
		method: 'get',
		url:'http://localhost:8000',
		headers: {
		Authorization: `Token ${token}`
		}

		}).then(res => setMessage(res.data.text)).catch(err => console.log(err))
		}
	}, [token])


	const SubmitHandle = e => {
		e.preventDefault()
		axios.post('http://localhost:8000/token/', {'username':username, 'password':password})
		.then(res => {
			setToken(res.data.token)
		})
		.catch(err => console.log(err))
	}
	return (
		<div className="App">

		{!token?
		<form style={formStyles} onSubmit={SubmitHandle}>
		<input value={csrftoken} type='hidden'/>
			<label htmlFor='username'>Username</label>
		<input style={inputStyles} name='username' value={username} onChange={e => setUsername(e.target.value)}/>
			<label htmlFor='password'>Password</label>
		<input style={inputStyles} type='password' name='password' value={password} onChange={e => setPassword(e.target.value)}/>
			<input style={submitStyles} type='submit' value='Log in'/>
		</form>
		:
		<h1>{message}</h1>
		}
		</div>
	);
}

export default App;
