import './App.css';
import axios from 'axios'
import {useEffect, useState} from 'react'
import {csrftoken} from './Token.js'

function App() {
	const [message, setMessage] = useState('')
	const [token, setToken] = useState('')
	const [username, setUsername] = useState('')
	const [password, setPassword] = useState('')

	useEffect(()=>{
		if (token){
		//axios.get('http://localhost:8000/', {'Authorization': `Token ${token}`})	
		//	.then(res => setMessage(res.data.text))
		//	.catch(err => console.log(err))
		//}
		axios({
		method: 'get',
		url:'http://localhost:8000',
		headers: {
		Authorization: `Token ${token}`
		}

		}).then(res => setMessage(res.data.text)).catch(err => console.log(err))
		}
	}, [token])


	const SubmitHandle = e => {
		e.preventDefault()
		axios.post('http://localhost:8000/token/', {'username':username, 'password':password})
		.then(res => {
			setToken(res.data.token)
		})
		.catch(err => console.log(err))
	}
	return (
		<div className="App">
	     	<h1>{message}</h1>
		{!token?
		<form onSubmit={SubmitHandle}>
		<input value={csrftoken} type='hidden'/>
			<label htmlFor='username'>Username</label>
		<input name='username' value={username} onChange={e => setUsername(e.target.value)}/>
			<label htmlFor='password'>Password</label>
		<input name='password' value={password} onChange={e => setPassword(e.target.value)}/>
			<input type='submit' value='Log in'/>
		</form>
		:
		<h1>{message}</h1>
		}
		</div>
	);
}

export default App;
